com.mirth.connect.plugins.javascriptstep.script:
//see outbound template for hard-coded MSH values

//Try to get MSH-4 from the Location's WIR ID
try{
	var location = msg['entry'].filter(function (entry) {
		return entry['resource']['resourceType'] === "Location"
	})[0]['resource'];

	tmp['MSH']['MSH.4']['MSH.4.1'] = location['identifier'].filter(function (id) {
		return id['system'] === "WIR"
	})[0]['value'];
} catch (e){
	if (!(e instanceof TypeError)){
		throw e;
	}
}

tmp['MSH']['MSH.7']['MSH.7.1'] = DateUtil.getCurrentDate("yyyyMMddHHmmss");
tmp['MSH']['MSH.10']['MSH.10.1'] = connectorMessage.getMessageId();

//JSON to PID mapping starts here

//msg is the inbound template (or json)
//tmp is the outbound template (or hl7 v2)

var patient = msg['entry'].filter(function (entry) {
	return entry['resource']['resourceType'] === "Patient"
})[0]['resource'];

//PID-3 patient id
var ids = [];

if (patient['id']) {
	var fhirId = [];
	fhirId[0] = patient['id'].toString();
	fhirId[3] = 'MassVaxx FHIR Server';
	fhirId[4] = 'FHIR Patient ID';

	ids.push(fhirId.join('^'));
}


for (var i = 0; i < getArrayOrXmlLength(patient['identifier']); i++) {
	var id = [];
	try{
		id[0] = patient['identifier'][i]['value'].toString();
		id[3] = patient['identifier'][i]['type']['coding'][0]['display'].toString();
		id[4] = patient['identifier'][i]['type']['coding'][0]['code'].toString();
	} catch (e) {
		//TypeError means that one of the above three values does not exist
		if (!(e instanceof TypeError)){
			throw e;
		}
	}
	if (id[0] && id[3] && id[4]) { 
		ids.push(id.join('^'));
	}
}
tmp['PID']['PID.3'] = ids.join('~');

//PID-5 patient name
tmp['PID']['PID.5']['PID.5.1'] = patient['name'][0]['family'];
tmp['PID']['PID.5']['PID.5.2'] = patient['name'][0]['given'][0];
tmp['PID']['PID.5']['PID.5.7'] = "L";  // Legal Name		

//PID-7 Birthdate 

var birthDate = patient['birthDate'];
tmp['PID']['PID.7']['PID.7.1'] = birthDate.split('-').join('');


//PID-8 Administrative Sex -- Needs to be edited for actualy values F/M

if(patient['gender'] == 'female'){  
	tmp['PID']['PID.8']['PID.8.1'] = 'F';
}
else if(patient['gender'] == "male"){
	tmp['PID']['PID.8']['PID.8.1'] = 'M';
	}

else{
	tmp['PID']['PID.8']['PID.8.1'] = 'U';
}

//PID-10 Race  -- MVS-528
try{
	var race = patient['extension'].filter(function (extension){
		return extension['url'] === "http://hl7.org/fhir/us/core/StructureDefinition/us-core-race"
	})[0]['extension'][0];
	tmp['PID']['PID.10']['PID.10.1'] = race['valueCoding']['code'];
	tmp['PID']['PID.10']['PID.10.2'] = race['valueCoding']['display'];
	tmp['PID']['PID.10']['PID.10.3'] = 'HL70005';
} catch (e) {
	//TypeError means race isn't in the extensions list or the resource has no extensions
	if (!(e instanceof TypeError)) throw e;
}


//PID-11 address

tmp['PID']['PID.11']['PID.11.1'] = patient['address'][0]['line'].join(", ");
tmp['PID']['PID.11']['PID.11.3'] = patient['address'][0]['city'];
tmp['PID']['PID.11']['PID.11.4'] = patient['address'][0]['state'];
tmp['PID']['PID.11']['PID.11.5'] = patient['address'][0]['postalCode'];
tmp['PID']['PID.11']['PID.11.6'] = patient['address'][0]['country'];
tmp['PID']['PID.11']['PID.11.7'] = 'M' // Mailing address

//PID-13 Home Phone Number 
var phoneNumber = patient['telecom'][0]['value']; // get phone number
var areaCode = phoneNumber.replace(/\D/g,'').substr(0, 3) 
var number = phoneNumber.replace(/\D/g,'').substr(3, 10) 

tmp['PID']['PID.13']['PID.13.2'] = 'HL70201';
tmp['PID']['PID.13']['PID.13.6'] = areaCode;
tmp['PID']['PID.13']['PID.13.7'] = number; 


// PID-22 Ethnic Group -- MVS-528

try{
	var ethnicGroup = patient['extension'].filter(function (extension){
		return extension['url'] === "http://hl7.org/fhir/us/core/StructureDefinition/us-core-ethnicity"
	})[0]['extension'][0];
	tmp['PID']['PID.22']['PID.22.1'] = ethnicGroup['valueCoding']['code'];
	tmp['PID']['PID.22']['PID.22.2'] = ethnicGroup['valueCoding']['display'];
	tmp['PID']['PID.22']['PID.22.3'] = 'CDCREC';
} catch (e) {
	//TypeError means ethnicGroup isn't in the extensions list or the resource has no extensions
	if (!(e instanceof TypeError)) throw e;
}

// WIR message may have 1 PD1 Segment

//PD1-11 - Publicity Code


//PD1-12 - Protection Indicator


//PD1-13 Effective date for PD1-12


//PD1-16 Immunization Registry Status


//PD1-17 Effective date for PD1-16


//PD1-18 Effective date for PD1-11

// NK1-1 Set ID 

tmp['NK1']['NK1.1'] = 1;

//NK1-2 Name of responsible person

tmp['NK1']['NK1.2']['NK1.2.1'] = patient['contact'][0]['name']['family'];
tmp['NK1']['NK1.2']['NK1.2.2'] = patient['contact'][0]['name']['given'][0]; 
tmp['NK1']['NK1.2']['NK1.2.7'] = "L";  // Legal Name

//NK1-3 Relationship 

tmp['NK1']['NK1.3']['NK1.3.1'] = patient['contact'][0]['relationship'][0]['coding'][0]['code']
tmp['NK1']['NK1.3']['NK1.3.3'] = 'HL70063'; 

//NK1-4 Address of responsible person 
try{
	var lines = [];
	for (var i = 0; i < getArrayOrXmlLength(patient['contact'][0]['address']['line']); i++) {
		var line = [];
		line[i] = patient['contact'][0]['address']['line'][i];
		lines.push(line.join(', '));
	}

	tmp['NK1']['NK1.4']['NK1.4.1'] = lines.join('');

	tmp['NK1']['NK1.4']['NK1.4.3'] = patient['contact'][0]['address']['city'];
	tmp['NK1']['NK1.4']['NK1.4.4'] = patient['contact'][0]['address']['state'];
	tmp['NK1']['NK1.4']['NK1.4.5'] = patient['contact'][0]['address']['postalCode'];
	tmp['NK1']['NK1.4']['NK1.4.6'] = patient['contact'][0]['address']['country'];
	tmp['NK1']['NK1.4']['NK1.4.7']= 'M' // Mailing address
} catch (e){
	//TypeError means the contact doesn't have enough information to fill out the address
	if (!(e instanceof TypeError)) throw e;
}


//NK1-5 Phone number

var phoneNumber = patient['contact'][0]['telecom'][0]['value']; // get phone number
var areaCode = phoneNumber.replace(/\D/g,'').substr(0, 3) 
var number = phoneNumber.replace(/\D/g,'').substr(3, 10) 

tmp['NK1']['NK1.5']['NK1.5.2'] = 'HL70201';
tmp['NK1']['NK1.5']['NK1.5.6'] = areaCode;
tmp['NK1']['NK1.5']['NK1.5.7'] = number;

// Grab immunization data
var immunization = msg['entry'].filter(function (entry){
    return entry['resource']['resourceType'] === "Immunization";
})[0]['resource'];

//ORC-1 Order Control - Must contatin value 'RE'
tmp['ORC']['ORC.1'] = 'RE';

//ORC-3 Filler Order Number(R) 
tmp['ORC']['ORC.3'] = immunization['id'];


//ORC-2 Placer Order Number (RE) 
//ORC-10 Entered By -- Who entered the order
//ORC-12 Ordering Provider
//ORC-17 Entering Organization

//Grab bundled resources


var immunization = msg['entry'].filter(function (entry){
    return entry['resource']['resourceType'] === "Immunization";
})[0]['resource'];

var location = msg['entry'].filter(function (entry){
    return entry['resource']['resourceType'] === "Location";
})[0]['resource'];

var actor = msg['entry'].filter(function (entry){
    return entry['resource']['resourceType'] === "Practitioner";
})[0]['resource'];


//RXA-1 Must be 0
tmp['RXA']['RXA.1'] = 0;

//RXA-2 Must be 1
tmp['RXA']['RXA.2'] = 1;

//RXA-3 Data of Administrationmsg['entry'][0]['resource']['occurrenceDateTime']

var occurenceDate = immunization['occurrenceDateTime'];
tmp['RXA']['RXA.3'] = occurenceDate.split('-').join(''); 

//RXA-5 Administered Code -- // NDC code?


if(immunization['manufacturer']['display'].includes('Pfizer')){
	tmp['RXA']['RXA.5']['RXA.5.1']  = '207';
	tmp['RXA']['RXA.5']['RXA.5.3']  = 'CVX';
}
else if(immunization['manufacturer']['display'].includes('Moderna')){
	tmp['RXA']['RXA.5']['RXA.5.1']  = '208';
	tmp['RXA']['RXA.5']['RXA.5.3']  = 'CVX';
}
else if(immunization['manufacturer']['display'].includes('Johnson')){
	tmp['RXA']['RXA.5']['RXA.5.1']  = '212';
	tmp['RXA']['RXA.5']['RXA.5.3']  = 'CVX';
}


//RXA-6 Administered Amount

tmp['RXA']['RXA.6'] = immunization['doseQuantity']['value'];

//RXA-7 Administerd Units

tmp['RXA']['RXA.7'] = immunization['doseQuantity']['code'];

//RXA-9 Administration Notes 

tmp['RXA']['RXA.9'] = 00;


//RXA-10 Administering Provider 
try{
	tmp['RXA']['RXA.10']['RXA.10.1'] = actor['identifier'][0]['value'];
	tmp['RXA']['RXA.10']['RXA.10.2'] = actor['name'][0]['family'];
	tmp['RXA']['RXA.10']['RXA.10.3'] = actor['name'][0]['given'][0];
	tmp['RXA']['RXA.10']['RXA.10.7'] = 'L';
} catch (e) {
	//TypeError means the Practitioner resource does not have enough information
	if (!(e instanceof TypeError)) throw e;
	tmp['RXA']['RXA.10']['RXA.10.1'] = actor['id'];
}


//RXA-15 Substance Lot Number

tmp['RXA']['RXA.15'] = immunization['lotNumber'];

//RXA-16 Substance Expiration Date

var expirationDate = immunization['expirationDate'];
tmp['RXA']['RXA.16'] = expirationDate.split('-').join(''); 


//RXA-17 Substance Manufacturer Name -- Reference to Organization 

if (immunization['manufacturer']['display'].includes('Pfizer')){
	tmp['RXA']['RXA.17']['RXA.17.1']  = 'PFR';
	tmp['RXA']['RXA.17']['RXA.17.2']  = 'Pfizer';
	tmp['RXA']['RXA.17']['RXA.17.3'] = 'MVX';
}
else if(immunization['manufacturer']['display'].includes('Moderna')){
	tmp['RXA']['RXA.17']['RXA.17.1']  = 'MOD';
	tmp['RXA']['RXA.17']['RXA.17.2']  = 'Moderna';
	tmp['RXA']['RXA.17']['RXA.17.3'] = 'MVX';
}
else if(immunization['manufacturer']['display'].includes('Johnson')){
	tmp['RXA']['RXA.17']['RXA.17.1']  = 'JNJ';
	tmp['RXA']['RXA.17']['RXA.17.2']  = 'Johnson and Johnson';
	tmp['RXA']['RXA.17']['RXA.17.3'] = 'MVX';
}


//RXA-21 Action Code
tmp['RXA']['RXA.21'] = 'A';

// Left for Q/A and questions
//RXA-11 Administer at Location -
//RXA-18 Refusal
//RXA-20 Completion Status

var immunization = msg['entry'].filter(function (entry){
    return entry['resource']['resourceType'] === "Immunization";
})[0]['resource'];

// RXR-1 Route of administration -- Immunization-route translation of NCIT to FHIR
var route = immunization['route']['coding'][0]['code'];

if(route == 'IM'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C28161';
}
else if(route == 'IDINJ'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38238';
}
else if(route == 'IVINJ'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38276';
}
else if(route == 'NASINHLC'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38284';
}
else if(route == 'PO'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38288';
}
else if(route == 'SQ'){
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38299';
}	
else{
	tmp['RXR']['RXR.1']['RXR.1.1'] = 'C38305';
}

tmp['RXR']['RXR.1']['RXR.1.3'] = 'NCIT';

// RXR-2 Administration Site
var site = immunization['site']['coding'][0]['code'];

if(site == 'LA'){
	tmp['RXR']['RXR.2']['RXR.2.1'] = 'LA';
	tmp['RXR']['RXR.2']['RXR.2.2'] = 'Left Arm';
}
else if(site == 'RA'){
	tmp['RXR']['RXR.2']['RXR.2.1'] = 'RA';
	tmp['RXR']['RXR.2']['RXR.2.2'] = 'Right Arm';
}

tmp['RXR']['RXR.2']['RXR.2.3'] = 'HL70163';

