mapper.select:
SELECT NAME AS "key", VALUE AS "value"
FROM CONFIGURATION
WHERE CATEGORY = #{value}

SELECT NAME AS "key", VALUE AS "value"
FROM CONFIGURATION
WHERE CATEGORY = #{category}
AND NAME in

	#{item}

SELECT VALUE
FROM CONFIGURATION
WHERE CATEGORY = #{category}
AND NAME = #{name}

mapper.update:
UPDATE CONFIGURATION
SET VALUE = #{value}
WHERE CATEGORY = #{category} AND NAME = #{name}

mapper.delete:
DELETE FROM CONFIGURATION
WHERE CATEGORY = #{category}

	AND NAME = #{name}
